package Com.StringMethods;

public class StringContains {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String Data="my is name hyma janu";
		//Contain Method is used to find the word in source string 
		System.out.println(Data.contains("hyma")); 
		System.out.println(Data.contains(" "));
		System.out.println(Data.contains("janu"));
		System.out.println(Data.contains("."));
		System.out.println(Data.contains("Geetha"));
	}

}
