package Com.StringMethods;

public class StringLength {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
String data = "My name is Hyma";
System.out.println(data.length()); //it displays size of the string or data or content
System.out.println("Length of the String is:"+ data.length());

//which is used store the size in the form of Integer & by using size number we can find particular position in the string 
int l= data.length()-1; 
System.out.println(l);
	}

}
